import React, {useState, useEffect} from 'react';
import axios from "axios";
import { Link } from 'react-router-dom';



const Userlist = () => {
const [umst, setUsers] = useState([]);

useEffect(()=>{
  getUsers();
},[]);

const getUsers = async () =>
{
    const response = await axios.get('http://localhost:5001/umst')
    setUsers(response.data);
}

const deleteUser =  async (id) => {
    try {
        await axios.delete(`http://localhost:5001/umst/${id}`);
        getUsers();
    } catch (error) {
        console.log(error);
    }
}

  return (
    <div className="columns mt-5 is-centered">
        <div className="column is-half">
        <Link to={'add'} className='button is-success'>Add New</Link>
        <table className='table is-striped is-fullwidth'>
            <thead>
                <tr>
                    <th>No</th>
                    <th>Nama Dokumen</th>
                    <th>Nomor Surat</th>
                    <th>Tanggal Terbit</th>
                    <th>Tanggal Exp</th>
                    <th>Link</th>
                    <th>Keterangan</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {umst.map((umst, index)=> (
                     <tr key={umst.id}>
                     <td>{index+1}</td>
                     <td>{umst.Nama_Dokumen}</td>
                     <td>{umst.Nomor_Surat}</td>
                     <td>{umst.Tanggal_Terbit}</td>
                     <td>{umst.Tanggal_Exp}</td>
                     <td>{umst.Link}</td>
                     <td>{umst.Keterangan}</td>
                     <td>
                        <div className="field is-flex is-justify-content-space-between">
                        <Link to={`edit/${umst.id}`} className="button is-small is-info">Edit</Link>
                        <button onClick={()=> deleteUser(umst.id)} className="button is-small is-danger ml-2">Delete</button>
                        </div>
                        
                     </td>
                 </tr>
                ))}
               
            </tbody>
        </table>
        </div>
    </div>
  )
}

export default Userlist
