import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Userlist = () => {
  const [legal, setUsers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage] = useState(10);  // Number of users per page (fixed value)
  const [totalUsers, setTotalUsers] = useState(0);  // Total number of users for pagination

  useEffect(() => {
    getUsers();
  }, [currentPage]);  // Reload data when the currentPage changes

  const getUsers = async () => {
    try {
      const response = await axios.get('http://192.168.88.103:5001/legal');
      setUsers(response.data.users);  // Assuming your API returns users in a "users" field
      setTotalUsers(response.data.totalCount);  // Assuming totalCount is provided by your API
    } catch (error) {
      console.error(error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://192.168.88.103:5001/legal/${id}`);
      getUsers();
    } catch (error) {
      console.log(error);
    }
  };

  // Calculate the index of the first and last user to display on the current page
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = legal.slice(indexOfFirstUser, indexOfLastUser);

  // Pagination controls
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(totalUsers / usersPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <Link to={'add'} className='button is-success'>Add New</Link>

        {totalUsers === 0 ? (
          <p>No users found.</p>  // Display this message if no users are found
        ) : (
          <>
            <table className='table is-striped is-fullwidth'>
              <thead>
                <tr>
                  <th>No</th>
                  <th>Nama Dokumen</th>
                  <th>Nomor Surat</th>
                  <th>Tanggal Terbit</th>
                  <th>Tanggal Exp</th>
                  <th>Site</th>
                  <th>Link</th>
                  <th>Keterangan</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {currentUsers.map((legal, index) => (
                  <tr key={legal.id}>
                    <td>{index + 1}</td>
                    <td>{legal.Nama_Dokumen}</td>
                    <td>{legal.Nomor_Surat}</td>
                    <td>{legal.Tanggal_Terbit}</td>
                    <td>{legal.Tanggal_Exp}</td>
                    <td>{legal.Site}</td>
                    <td>{legal.Link}</td>
                    <td>{legal.Keterangan}</td>
                    <td>
                      <div className="field is-flex is-justify-content-space-between">
                        <Link to={`edit/${legal.id}`} className="button is-small is-info">Edit</Link>
                        <button onClick={() => deleteUser(legal.id)} className="button is-small is-danger ml-2">Delete</button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Pagination Controls */}
            <div className="pagination is-centered">
              <button
                className="pagination-previous"
                onClick={() => setCurrentPage(currentPage - 1)}
                disabled={currentPage === 1}
              >
                Previous
              </button>
              <button
                className="pagination-next"
                onClick={() => setCurrentPage(currentPage + 1)}
                disabled={currentPage === pageNumbers.length}
              >
                Next
              </button>
              <ul className="pagination-list">
                {pageNumbers.map((number) => (
                  <li key={number}>
                    <button
                      className={`pagination-link ${currentPage === number ? 'is-current' : ''}`}
                      onClick={() => setCurrentPage(number)}
                    >
                      {number}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Userlist;
