import React, { useState, useEffect } from 'react';
import axios from "axios";
import { Link } from 'react-router-dom';

const Userlist = () => {
  const [legal, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage] = useState(5);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async () => {
    const response = await axios.get('http://192.168.88.103:5001/legal');
    setUsers(response.data);
  }

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://192.168.88.103:5001/legal/${id}`);
      getUsers();
    } catch (error) {
      console.log(error);
    }
  }

  const filteredUsers = legal.filter(user =>
    user.Nama_Dokumen.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.Keterangan.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(filteredUsers.length / usersPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <div className="columns is-centered mt-5">
      <div className="column is-half">
        <div className="card">
          <header className="card-header">
            <p className="card-header-title">Tambah Dokumen Baru</p>
          </header>
          <div className="card-content">
            <form onSubmit={saveDocument}>
              <div className="field">
                <label className="label">Nama Dokumen</label>
                <div className="control">
                  <input 
                    type="text" 
                    className="input" 
                    placeholder="Masukkan Nama Dokumen"
                    value={namaDokumen}
                    onChange={(e) => setNamaDokumen(e.target.value)}
                    required
                  />
                </div>
              </div>
              
              <div className="field">
                <label className="label">Nomor Surat</label>
                <div className="control">
                  <input 
                    type="text" 
                    className="input" 
                    placeholder="Masukkan Nomor Surat"
                    value={nomorSurat}
                    onChange={(e) => setNomorSurat(e.target.value)}
                  />
                </div>
              </div>

              <div className="field">
                <label className="label">Tanggal Terbit</label>
                <div className="control">
                  <input 
                    type="date" 
                    className="input" 
                    value={tanggalTerbit}
                    onChange={(e) => setTanggalTerbit(e.target.value)}
                    required
                  />
                </div>
              </div>

              <div className="field">
                <label className="label">Tanggal Exp</label>
                <div className="control">
                  <input 
                    type="date" 
                    className="input" 
                    value={tanggalExp}
                    onChange={(e) => setTanggalExp(e.target.value)}
                  />
                </div>
              </div>

              <div className="field">
                <label className="label">Site</label>
                <div className="control">
                  <input 
                    type="text" 
                    className="input" 
                    placeholder="Masukkan Site"
                    value={site}
                    onChange={(e) => setSite(e.target.value)}
                  />
                </div>
              </div>

              <div className="field">
                <label className="label">Link</label>
                <div className="control">
                  <input 
                    type="url" 
                    className="input" 
                    placeholder="Masukkan Link"
                    value={link}
                    onChange={(e) => setLink(e.target.value)}
                  />
                </div>
              </div>

              <div className="field">
                <label className="label">Keterangan</label>
                <div className="control">
                  <textarea 
                    className="textarea" 
                    placeholder="Masukkan Keterangan"
                    value={keterangan}
                    onChange={(e) => setKeterangan(e.target.value)}
                  ></textarea>
                </div>
              </div>

              <div className="field is-grouped is-grouped-right">
                <p className="control">
                  <button type="submit" className="button is-success">Simpan</button>
                </p>
                <p className="control">
                  <button type="button" className="button is-light" onClick={() => history.push('/')}>Batal</button>
                </p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddDocument;
